var util = require('util');
var EventEmitter = require('events').EventEmitter;
var VK = require('vk');
var MongoClient = require('mongodb').MongoClient;

var Spammer = function (){
  EventEmitter.call(this);

  // this.vkLoginResponse = this.vkLoginResponse.bind(this);
  // this.vkLogOutResponse = this.vkLogOutResponse.bind(this);
  // this.onResponseMembersOfGroup = this.onResponseMembersOfGroup.bind(this);

}

util.inherits(Spammer, EventEmitter);

var _Class = Spammer;

_Class.prototype.addEventListeners = function () {
  this.on('response_members_of_group', this.onResponseMembersOfGroup);
  this.on('synchronized_data_with_database', this.onSynchronizedDataWithDb)
}

_Class.prototype.removeEventListeners = function () {
  // this.on('response_members_of_group', this.onResponseMembersOfGroup);
  // this.on('synchronized_data_with_database', this.onSynchronizedDataWithDb);
}

_Class.prototype.start = function() {
  var self = this;

  global.started = true;

  window.primaryLog('Приложение запущено!');

  this.addEventListeners(); // при старте добавляем обработчики событий

  this.connectToDb(function(db){
    self.db = db;

    window.secondaryLog('Подключение к базе данных прошло успешно!');

    self.synchronizeDataWithDb();

  });
}

_Class.prototype.stop = function() {
  this.removeEventListeners();
}

_Class.prototype.connectToDb = function(callback){
  window.primaryLog('Подключаемся к базе данных');

  MongoClient.connect('mongodb://127.0.0.1:27017/spammerdb', function(err, db) {
    if(err) throw err;

    callback(db);
  });
}

_Class.prototype.synchronizeDataWithDb = function(){
  var self = this;

  var getMembers = function(offset){

    if(!offset) offset = 0;

    window.primaryLog('Получение участников группы <strong>' + global.main_group + '</strong>');

    var options = {
      gid: global.main_group,
      access_token: currentUser.access_token,
      count: 1000,
      sort: 'time_desc'
    }

    if(offset > 0){
      _.extend(options, {offset: offset});
    }

    VK.request('groups.getMembers',
      options,
      function(error, response){
        if(error){
          window.secondaryLog('Произошла ошибка получения данных группы <strong>' + group_id + '</strong>');

          window.secondaryLog(error.error_msg);
          window.secondaryLog(util.inspect(error));

        } else {
          window.secondaryLog('Получение ' + (offset + response.users.length) + ' из ' + response.count + ' участников группы <strong>' + group_id + '</strong> прошло успешно');

          _.extend(response, {offset: offset + response.users.length});

          addMembersToDb(response);
        }
      }
    );
  }

  var addMembersToDb = function(response) {
    var db_users = self.db.collection('users');

    window.secondaryLog('Синхронизация полученных данных с базой данной...');

    self.filterByDb(response.users, function(user_ids){
      db_users.insert(
      _.map(user_ids, function(id){ return {_id: id} }),
        function(err, objects){
          if (err) window.secondaryLog(err.message);

          window.secondaryLog('Синхронизация данных с базой данной завершена');

          if(response.count > response.offset){
            setTimeout(function(){
              getMembers(response.offset);
            }, 1000);

          } else {
            window.primaryLog('Все участники группы обработаны.');

            //emitter.emmit('synchronized_data_with_database');
          }
        }
      );
    });
  }

  getMembers();
}

_Class.prototype.onSynchronizedDataWithDb = function(){
  window.secondaryLog('Синхронизация с базой данных завершена.');

  window.secondaryLog('Предварительный выход из аккаунта.');

  VK.logOut(function(message){
    currentUser = {}; // сброс пользователя

    window.secondaryLog(message);

    setTimeout(function(){
      self.vkLogin();
    }, 2000); // выдерживаем интервал после выхода

  });
}


_Class.prototype.vkLogin = function (){
  auth_user = global.logins[global.login_number];

  if(!auth_user) {
    window.secondaryLog('Пользователи все обработаны');

    this.db.close();

    return
  }

  window.console.log(global.login_number);
  window.console.log(auth_user);
  var login = auth_user[1];
  var pass  = auth_user[2];

  global.login_number += 1 // сдвигаем на следующего пользователя

  // if(global.logins.length > global.login_number + 1){
  //   global.login_number += 1 // сдвигаем на следующего пользователя
  // }
  // else {
  //   global.login_number = 0; // передвагаем на начала списка
  // }

  window.console.log(global.login_number);

  window.primaryLog('Идет авторизация пользователя <strong>' + auth_user[0] + '</strong>');

  VK.authorize(login, pass, this.vkLoginResponse);
}

_Class.prototype.vkLoginResponse = function(error, response) {
  var self = this;

  if(error) {
    window.primaryLog('Пользователь не авторизовался!');
    window.secondaryLog(util.inspect(error));

    this.vkLogin();

  } else if(response) {
    currentUser = response;

    window.primaryLog('Пользователь <strong>' + currentUser.user_id + '</strong> авторизовался успешно!');
    this.renderSidebar();

    setTimeout(function(){
      self.getProfile();
    }, 1000); //Выдерживаем

  } else {
    window.primaryLog('Пользователь не авторизовался!');
    window.secondaryLog('Метод авторизации не вернул никаких данных для пользователя <strong>' + auth_user[0] + '</strong>');
    window.secondaryLog('Начинаем авторизацию следующего пользователя');

    this.vkLogin();
  }
}

_Class.prototype.vkLogout = function (){
  VK.logOut(this.vkLogOutResponse);
}

_Class.prototype.vkLogOutResponse = function(message){
  currentUser = {};
  window.primaryLog(message);
}


_Class.prototype.getProfile = function(){
  var self = this;

  VK.request('getProfiles',
    {
      access_token: currentUser.access_token,
      fields: 'counters'
    },
    function(error, response){
      if(error){
        window.secondaryLog('Произошла ошибка получения данных профиля пользователя <strong>' + currentUser.user_id + '</strong>');

        window.secondaryLog(error.error_msg);
        window.secondaryLog(util.inspect(error));

      } else {
        window.console.log(response[0]);
        _.extend(currentUser, response[0]);

        window.secondaryLog('Данные получены успешно');
        //window.secondaryLog(util.inspect(currentUser));
        window.secondaryLog('Количество друзей - <strong>' + currentUser.counters.friends + '</strong>');

        setTimeout(function(){
          self.getFrendIds();
        }, 2000);
      }
    }
  );
}

_Class.prototype.getFrendIds = function(){
  var self = this;

  window.secondaryLog('Делаем запрос на получение ids друзей пользователя <strong>' + currentUser.user_id + '</strong>');

  VK.request('friends.get',
    {
      access_token: currentUser.access_token
    },
    function(error, response){
      if(error){
        window.secondaryLog('Произошла ошибка получения ids друзей пользователя <strong>' + currentUser.user_id + '</strong>');

        window.secondaryLog(error.error_msg);
        window.secondaryLog(util.inspect(error));

      } else {
        window.secondaryLog('Получение ids друзей пользователя <strong>' + currentUser.user_id + '</strong> прошло успешно.');

        _.extend(currentUser, {friend_ids: response});

        self.updateUsersDataInDb();
      }
    }
  );
}

_Class.prototype.updateUsersDataInDb = function(){
  var self = this;

  window.secondaryLog('Синхронизация полученных данных с базой данной...');

  var users = this.db.collection('users');

  this.filterByDb(currentUser.friend_ids, function(friend_ids){
    users.insert(
    _.map(friend_ids, function(id){ return {_id: id} }),
      function(err, objects){
        if (err) window.secondaryLog(err.message);

        window.secondaryLog('Синхронизация данных с базой данной завершена');

        self.startSpammWork();
      }
    );
  });

}

_Class.prototype.startSpammWork = function() {
  var self = this;
  var members = [];
  var offset = 0;

  var group_id = global.groups[global.group_number];

  if(global.groups.length > global.group_number + 1){
    global.group_number += 1 // сдвигаем на следующего пользователя
  } else {
    global.group_number = 0; // передвагаем на начала списка
  }

  window.primaryLog('Получение данных группы <strong>' + group_id + '</strong>');

  this.getMembersOfGroup(group_id, 0);
}

_Class.prototype.getMembersOfGroup = function(group_id, offset){
  var self = this;

  window.primaryLog('Получение участников группы <strong>' + group_id + '</strong>');

  var options = {
    gid: group_id,
    access_token: currentUser.access_token,
    count: 100,
    sort: 'time_desc'
  }

  if(offset > 0){
    _.extend(options, {offset: offset});
  }

  VK.request('groups.getMembers',
    options,
    function(error, response){
      if(error){
        window.secondaryLog('Произошла ошибка получения данных группы <strong>' + group_id + '</strong>');

        window.secondaryLog(error.error_msg);
        window.secondaryLog(util.inspect(error));

      } else {
        window.secondaryLog('Получение участников группы <strong>' + group_id + '</strong> прошло успешно');

        setTimeout(function(){
          self.emit('response_members_of_group',
          _.extend(response, {
              offset: offset + response.users.length,
              group_id: group_id
            })
          );
        }, 2000);
      }
    }
  );
}

_Class.prototype.onResponseMembersOfGroup = function(members_group_response) {
  var self = this;

  window.console.log(members_group_response.offset);

  this.filterByDb(members_group_response.users, function(users){
    VK.request('users.get',
      {
        uids: users.join(','),
        access_token: currentUser.access_token,
        fields: 'online'
      },
      function(error, response){
        if(error){
          window.secondaryLog('Произошла ошибка получения расширенной информации участников группы <strong>' + members_group_response.group_id + '</strong>');

          window.secondaryLog(error.error_msg);
          window.secondaryLog(util.inspect(error));

        } else {
          window.secondaryLog('Получение расширенной информации участников группы <strong>' + members_group_response.group_id + '</strong> прошло успешно');

          self.addToFriends(response, 0);
        }
      }
    );
  });

  var getGroupMembersAgain = function(){
    if(members_group_response.count > members_group_response.offset){
      setTimeout(function(){
        self.getMembersOfGroup(members_group_response.group_id, members_group_response.offset);
      }, 2000);
    } else {
      window.primaryLog('Все участники группы обработаны.');

      // VK.logOut(function(message){
      //   currentUser = {}; // сброс пользователя

      //   window.secondaryLog(message);

      //   setTimeout(function(){
      //     self.vkLogin();
      //   }, 2000); // выдерживаем интервал после выхода

      // });
    }
  }

  self.removeListener('get_group_members_again', getGroupMembersAgain);

  self.once('get_group_members_again', getGroupMembersAgain);
}

_Class.prototype.filterByDb = function(user_ids, callback){
  window.console.log(user_ids.length);
  var db_users = this.db.collection('users');
  var db_user_ids;
  var filtered_user_ids = [];

  db_users.find({_id: {$in: user_ids}}).toArray(function(err, objects){
    window.console.log('Проверка пользователей в базе');
    window.console.log(objects);

    db_user_ids = _.map(objects, function(o){ return o._id });

    user_ids.forEach(function(id){
      if(!_.contains(db_user_ids, id)){
        filtered_user_ids.push(id);
      }
    });

    window.console.log(filtered_user_ids.length);

    callback(filtered_user_ids);
  });
}

_Class.prototype.addToFriendAfterCaptcha = function(user_id, text, captcha_sid, users, count){
  var self = this;

  VK.request('friends.add',
    {
      uid: user_id,
      captcha_sid: captcha_sid,
      captcha_key: text,
      access_token: currentUser.access_token
    },
    function(error, response){
      if(error){
        //window.secondaryLog('Произошла ошибка получения расширенной информации участников группы <strong>' + members_group_response.group_id + '</strong>');

        window.console.log(error.error_code);

        if(error.error_code == 1 || error.error_code == 0){
          // превышен лими добавления в друзья вызодим из пользователя

          window.secondaryLog('Превышен лимит на добавления в друзья!');

          VK.logOut(function(message){
            currentUser = {}; // сброс пользователя

            window.secondaryLog(message);

            setTimeout(function(){
              self.vkLogin();
            }, 2000); // выдерживаем интервал после выхода

          });

        } else if(error.error_code == 14){
          window.secondaryLog('Cнова пришла капча на добавление пользователя <strong>' + user_id + '</strong> в друзья');

          setTimeout(function(){
            if(users.length > count + 1){
              self.addToFriends(users, count);
            } else {
              self.emit('get_group_members_again');
            }

          }, 5000);

        } else {
          window.secondaryLog(error.error_msg);
          window.secondaryLog(util.inspect(error));
        }

      } else {
        //window.secondaryLog('Получение расширенной информации участников группы <strong>' + members_group_response.group_id + '</strong> прошло успешно');

        window.console.log(response);

        global.success_added += 1;

        self.renderSidebar();
        var db_users = self.db.collection('users');

        db_users.insert({_id: user_id}, function(err, objects){
          if (err) window.secondaryLog(err.message);

          window.secondaryLog('Добавлен новый пользователь в базу данных.');
        });

        setTimeout(function(){
          if(users.length > count + 1){
            self.addToFriends(users, count);
          } else {
            self.emit('get_group_members_again');
          }

        }, 5000);
      }
    }
  );
}

