var ejs = require('ejs');       // шаблонизатор
var fs = require('fs');        // модуль для работы с файловой системой
var path = process.cwd() + '/views';

var Viewer = function (){}

Viewer.prototype.render = function (filename, args){
  if(!args) args = {};

  var str = fs.readFileSync(path + '/' + filename, 'utf8');

  args.filename = path + '/' + filename;

  return ejs.render(str, args);
}

module.exports = Viewer;